function offset(el) {
    var rect = el.getBoundingClientRect(),
    scrollLeft = window.pageXOffset || document.documentElement.scrollLeft,
    scrollTop = window.pageYOffset || document.documentElement.scrollTop;
    return { top: rect.top + scrollTop, left: rect.left + scrollLeft }
}

// Surround matches with <strong></strong>
function highlightMatches(string, matches) {
	let array = [];

	// Start of string
	array.push(string.slice(0, matches[0][0]));

	for (let i = 0; i < matches.length; i++) {
		// Between highlights
		if (i != 0) {
			array.push(string.slice(matches[i-1][1]+1, matches[i][0]));
		}

		// Start <strong>
		array.push('<strong>');

		// Highlighted text
		array.push(string.slice(matches[i][0], matches[i][1]+1));

		// End </strong>
		array.push('</strong>');
	}

	// End of string
	array.push(string.slice(matches[matches.length-1][1]+1, string.length));

	return array.join('');
}

// Swap color mode
dark_toggle.onclick = function() {
	if (document.body.classList.contains('dark')) {
		this.innerHTML = "Dark mode";
		document.body.classList.remove('dark');
	} else {
		this.innerHTML = "Light mode";
		document.body.classList.add('dark');
	}
	return false;
};

// Match system dark mode
if (window.matchMedia("(prefers-color-scheme: dark)").matches) {
	dark_toggle.innerHTML = "Light mode";
	document.body.classList.add('dark');
}

// Select an option in the autocomplete
function autocompleteSelect(id) {
	console.log(id);
	autocomplete.classList.remove('show');
	const block = document.createElement('span');
	block.classList.add('project');
	block.appendChild(document.createTextNode('CLEANAWAY CLEANAVIEW TRUCK TRAIL CR1 JULY19'));
	block.setAttribute('contenteditable', 'false');
	textarea.lastChild.remove();
	textarea.appendChild(block);
	textarea.appendChild(document.createTextNode('\u200C'));
	textarea.focus();
}

// Textarea oninput
textarea.oninput = function() {
	const children = textarea.childNodes;
	if (textarea.lastChild && textarea.lastChild.nodeType === Node.TEXT_NODE && /\S/.test(textarea.lastChild.nodeValue) && textarea.lastChild.nodeValue.replace(/\u200C/ug, '').length != 0) {
		empty = false;
		console.log(textarea.lastChild.nodeValue.replace(/\u200C/gu, '').length);
	} else {
		empty = true;
	}

	if (empty) {
		updateState(WAITING_FOR_PROJECT);
		autocomplete.classList.remove('show');
	} else {
		updateState(TYPING_PROJECT);

		const results = search(projects, textarea.lastChild.nodeValue, [
			'project.name',
			'client.name'
		]);

		console.log(results);

		autocomplete.innerHTML = '';
		for (let i = 0; i < Math.min(results.length, 10); i++) {
			const result = document.createElement('span');
			result.innerHTML = highlightMatches(results[i].item.project.name, results[i].matches[0].indices);
			const subResult = document.createElement('span');
			subResult.innerHTML = highlightMatches(results[i].item.client.name, results[i].matches[1].indices);

			result.appendChild(subResult);
			result.dataset.id = results[i].item.id;
			if (i == 0) {
				result.classList.add('selected');
			}
			result.onclick = function() {
				autocompleteSelect(this.dataset.id);
			};
			autocomplete.appendChild(result);
		}

		const range = document.createRange();
		range.selectNode(textarea.lastChild);

		const pos = offset(range);
		autocomplete.classList.add('show');
		autocomplete.style.top = 'calc(' + pos.top + 'px + 1.5em)';
		autocomplete.style.left = 'calc(' + pos.left + 'px - .5em)';
	}
};

// Textarea onkeydown
textarea.onkeydown = function(e) {
	console.log(e);
	console.log(/\u200C/.test(window.getSelection().baseNode.data[window.getSelection().baseOffset-1]));

	if (/\u200B/.test(window.getSelection().toString())) {
		e.preventDefault();
	}

	if ((e.keyCode == 13 || e.keyCode == 9) && autocomplete.classList.contains('show')) { // Enter or tab to autocomplete
		e.preventDefault();
		const cur = autocomplete.querySelector('.selected');
		autocompleteSelect(cur.dataset.id);
	} else if (e.keyCode == 38) { // Arrow up
		if (autocomplete.classList.contains('show')) {
			e.preventDefault();
			const cur = autocomplete.querySelector('.selected');
			cur.classList.remove('selected');
			if (cur == cur.parentElement.firstChild) {
				cur.parentElement.lastChild.classList.add('selected');
			} else {
				cur.previousSibling.classList.add('selected');
			}
		}
	} else if (e.keyCode == 13 || e.keyCode == 0) { // Enter or space
		if (!autocomplete.classList.contains('show')) {
			e.preventDefault();
		}
	} else if (e.keyCode == 40) { // Arrow down
		if (autocomplete.classList.contains('show')) {
			e.preventDefault();
			const cur = autocomplete.querySelector('.selected');
			cur.classList.remove('selected');
			if (cur == cur.parentElement.lastChild) {
				cur.parentElement.firstChild.classList.add('selected');
			} else {
				cur.nextSibling.classList.add('selected');
			}
		}
	} else if (e.keyCode == 8) { // Backspace
		if (/\u200C/.test(window.getSelection().baseNode.data[window.getSelection().baseOffset-1])) {
			e.preventDefault();
		}
	}
};

// Update state and perform any actions that depend on state
function updateState(newState) {
	state = newState;

	if (state == WAITING_FOR_PROJECT) {
		textarea.setAttribute('placeholder', 'Start typing a project name...');
	} else if (state == WAITING_FOR_TASK) {
		textarea.setAttribute('placeholder', 'Start typing a task...');
	} else if (state == WAITING_FOR_TIME) {
		textarea.setAttribute('placeholder', 'Start typing a start time...');
	} else {
		textarea.setAttribute('placeholder', '');
	}
}

// Use Fuse.js to search through projects/tasks
function search(list, term, keys) {
	const options = {
		shouldSort: true,
		includeMatches: true,
		threshold: 0.6,
		location: 0,
		distance: 100,
		maxPatternLength: 32,
		minMatchCharLength: 1,
		keys: keys
	};
	const fuse = new Fuse(list, options);
	return fuse.search(term);
	//return test;
}

